mapper.select:
SELECT A.selliboard, A.writeriuser, A.category, A.manufacturer, A.model, A.detail_model, A.likesum , A.price, A.color, A.fuel, A.distan_driven, A.gearbox, A.trading_area, A.car_number, A.hits , B.mainimg FROM car_sell_board A LEFT JOIN car_image B ON A.selliboard = B.selliboard where A.category = #{category}
ORDER BY A.likesum desc
ORDER BY A.price desc
ORDER BY A.price
ORDER BY A.selliboard desc
LIMIT ${startIdx}, ${recordCount}

SELECT A.selliboard, A.writeriuser, A.category, A.manufacturer, A.model, A.detail_model , A.price, A.color, A.fuel, A.distan_driven, A.gearbox, A.trading_area, A.car_number , B.mainimg FROM car_sell_board A LEFT JOIN car_image B ON A.selliboard = B.selliboard
WHERE A.manufacturer = #{manufacturer}
AND A.model = #{model}
AND A.detail_model = #{detail_model}
ORDER BY A.likesum desc
ORDER BY A.price desc
ORDER BY A.price
ORDER BY A.selliboard desc
LIMIT ${startIdx}, ${recordCount}

SELECT A.selliboard, A.writeriuser, A.category, A.manufacturer, A.model, A.detail_model
     , A.price, A.color, A.fuel, A.distan_driven, A.gearbox, A.trading_area, A.car_number
     , B.mainimg
FROM car_sell_board A
         LEFT JOIN car_image B
                   ON A.selliboard = B.selliboard


AND A.manufacturer IN

        #{com}



    AND A.distan_driven >= ${Min_Mileage}


    AND A.distan_driven <= ${Max_Mileage}


    AND A.price >= ${Min_price}


    AND A.price <= ${Max_price}


    AND A.gearbox IN

        #{gearbox}



    AND A.fuel IN

        #{fuel}



    AND A.trading_area IN

        #{aria}


        AND A.category = #{category}


    ORDER BY A.likesum desc


    ORDER BY A.price desc


    ORDER BY A.price


    ORDER BY A.selliboard desc


    LIMIT  ${startIdx}, ${recordCount}

mapper.delete:
DELETE FROM likes
WHERE selliboard = ${selliboard}
  AND likesiuser =${likesiuser}

mapper.select:
SELECT count(*)
FROM likes
WHERE selliboard= ${selliboard}
 AND likesiuser = ${likesiuser}


SELECT ceil(COUNT(selliboard) / ${recordCount}) AS result
FROM car_sell_board
WHERE category=#{category}

SELECT ceil(COUNT(selliboard) / ${recordCount}) AS result
FROM car_sell_board


        AND manufacturer IN

            #{com}



        AND distan_driven >= ${Min_Mileage}


        AND distan_driven <= ${Max_Mileage}


        AND price >= ${Min_price}


        AND price <= ${Max_price}


        AND gearbox IN

            #{gearbox}



        AND fuel IN

            #{fuel}



        AND trading_area IN

            #{aria}


    AND category = #{category}

SELECT ceil(COUNT(selliboard) / ${recordCount}) AS result
FROM car_sell_board


    AND manufacturer = #{manufacturer}


    AND model = #{model}


    AND detail_model = #{detail_model}

mapper.update:
update car_sell_board
set likesum = likesum + 1
where selliboard = ${selliboard}

update car_sell_board
set likesum = likesum - 1
where selliboard = ${selliboard}

update car_sell_board
set hits = hits + 1
where selliboard = ${selliboard}
